import React, { useEffect, useState } from 'react';
import Constants from 'expo-constants';
import {View, StyleSheet, TouchableOpacity, Image, Text, SafeAreaView, Linking} from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import {RectButton} from 'react-native-gesture-handler'
import {Feather as Icon, FontAwesome} from '@expo/vector-icons'
import api from '../../services/api';
import * as MailComposer from 'expo-mail-composer';

interface Params {
  recycler_id: number;
};

interface Data {
  recyclerFiltered: {
    name: string;
    image: string;
    city: string;
    uf: string;
    email: string;
    whatsapp: string;
  }
  items: {
    title: string;
  }[]
}

const Detail = () => {
  const [data, setData] = useState<Data>({} as Data);
  const navigation = useNavigation();
  const route = useRoute();

  const routeParams = route.params as Params;

  const handleNavigateBack = () => {
    navigation.goBack();
  }

  const handleComposeMail = () => {
    MailComposer.composeAsync({
      subject: 'Interesse na coleta de res√≠duos',
      recipients: [data.recyclerFiltered.email],      
    });
  };

  const handleWhatsapp = () => {
    Linking.openURL(`whatsapp://send?phone=${data.recyclerFiltered.whatsapp}`)
  };

  useEffect(() => {
    api.get(`recycler/${routeParams.recycler_id}`)
    .then(response => {
      setData(response.data);
    });
  }, []);

  if (!data.recyclerFiltered) {
    return null;
  }

  return (
    <SafeAreaView style={{flex: 1}}>
      <View style={styles.container}>
        <TouchableOpacity onPress={handleNavigateBack}>
          <Icon name='arrow-left' size={20} color='#34cb79' />
        </TouchableOpacity>

        <Image style={styles.pointImage} source={{uri: data.recyclerFiltered.image}}/>
        <Text style={styles.pointName}>{data.recyclerFiltered.name}</Text>
        <Text style={styles.pointItems}>{data.items.map(item => item.title).join(', ')}</Text>

        <View style={styles.address}>
          <Text style={styles.addressTitle}>Endere√ßo</Text>
          <Text style={styles.addressContent}>{data.recyclerFiltered.city}, {data.recyclerFiltered.uf}</Text>
        </View>
      </View>
      <View style={styles.footer}>
        <RectButton style={styles.button} onPress={handleWhatsapp}>
          <FontAwesome name='whatsapp' size={20} color='#FFF' />
          <Text style={styles.buttonText}>Whatsapp</Text>
        </RectButton>
        <RectButton style={styles.button} onPress={handleComposeMail}>
          <Icon name='mail' size={20} color='#FFF' />
          <Text style={styles.buttonText}>E-mail</Text>
        </RectButton>
      </View>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 32,
    paddingTop: 20 + Constants.statusBarHeight,
  },

  pointImage: {
    width: '100%',
    height: 120,
    resizeMode: 'cover',
    borderRadius: 10,
    marginTop: 32,
  },

  pointName: {
    color: '#322153',
    fontSize: 28,
    fontFamily: 'Ubuntu_700Bold',
    marginTop: 24,
  },

  pointItems: {
    fontFamily: 'Roboto_400Regular',
    fontSize: 16,
    lineHeight: 24,
    marginTop: 8,
    color: '#6C6C80'
  },

  address: {
    marginTop: 32,
  },

  addressTitle: {
    color: '#322153',
    fontFamily: 'Roboto_500Medium',
    fontSize: 16,
  },

  addressContent: {
    fontFamily: 'Roboto_400Regular',
    lineHeight: 24,
    marginTop: 8,
    color: '#6C6C80'
  },

  footer: {
    borderTopWidth: StyleSheet.hairlineWidth,
    borderColor: '#999',
    paddingVertical: 20,
    paddingHorizontal: 32,
    flexDirection: 'row',
    justifyContent: 'space-between'
  },

  button: {
    width: '48%',
    backgroundColor: '#34CB79',
    borderRadius: 10,
    height: 50,
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center'
  },

  buttonText: {
    marginLeft: 8,
    color: '#FFF',
    fontSize: 16,
    fontFamily: 'Roboto_500Medium',
  },
});

export default Detail
